import React, { useState, useEffect } from "react";

let nextId = 0;
function App() {
  const [inputText, setInputText] = useState("");
  const [itemList, setItemList] = useState([]);
  const [line, setLine] = useState(false);
  const [effect, setEffect] = useState(false);
  useEffect(()=> {
      const items = JSON.parse(localStorage.getItem('itemList'));

      if(items ){
        console.log(items);
        console.log(items.inputText);
        setItemList(items);
        // setItemList((prevValue) => {
        //   return [...prevValue, { id: nextId++, inputText: inputText }];
        // });
      }
    localStorage.clear();
  //  console.log(localStorage);
  }, [])
  useEffect(() => {
    // console.log('item set');
    if(itemList.length === 0){
      console.log('empty')
    }else{
      localStorage.setItem('itemList', JSON.stringify(itemList));
    }

    // localStorage.clear();
    console.log(localStorage);
    // console.log(itemList);
  }, [effect])

  function handleClick(event) {
    // console.log(event.target.name);
    setItemList((prevValue) => {
      return [...prevValue,   inputText ];
    });
    setInputText("");
    setEffect((val) => {
      return !val
    });
  }

  function handleChange(event) {
    const newValue = event.target.value;
    // console.log(newValue);
    setInputText(newValue);
  }
  function handleLine(event){
    if(event.target.style.textDecoration){
      event.target.style.removeProperty('text-decoration');
    }else{
      event.target.style.setProperty('text-decoration','line-through');
    }
    // console.log(event.target.style.textDecoration)
  };

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onKeyDown={(e)=>{
          if(e.key==="Enter"){
            if(inputText.length>0){
              handleClick()
            }
          }
        }} value={inputText} onChange={handleChange} type="text" />
        <button onClick={handleClick}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {itemList.map((item,index) => (
            <li  key={index} onClick={handleLine}>
              {item}
              <button 
                style={{ float: "right" }}
                onClick={() => {
                  setItemList(itemList.filter((a) => a.id !== item.id));
                  // localStorage.removeItem('itemList');
                }}
              >
                ðŸ—‘
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
